import router from "@/router";
import axios from "axios";

const config = axios.interceptors.request.use(function (config) {
    config.headers = {
        "user-token": localStorage.getItem("isLogin")
    }
    // 在发送请求之前做些什么
    return config;
}, function (error) {
    // 对请求错误做些什么
    return Promise.reject(error);
});

