// import HomePage from '@/components/HomePage.vue'
// import Hello from '@/components/Hello.vue'
import HomePage from '@/components/HomePage.vue';
import UserPage from '@/components/UserPage.vue';
import Login from '@/views/Login.vue'
import MainPage from '@/views/MainPage.vue';

import { createRouter, createWebHashHistory } from 'vue-router'

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/',
    name: 'MainPage',
    component: MainPage,
  }
]

const router = createRouter({
  history: createWebHashHistory(),
  routes
})

let routeFlag = false;

export function resetRouter() {
  return new Promise(() => {
    let routeLists = JSON.parse(localStorage.getItem('routeLists'));
    routeLists.forEach(route => {
      if (router.resolve(route.link) != null)
        router.removeRoute(route.pageName)
    })
  })

}
function addRoutes() {
  resetRouter();
  let routeLists = JSON.parse(localStorage.getItem('routeLists'));
  routeLists.forEach(route => {
    router.addRoute("MainPage", {
      path: route.link,
      name: route.pageName,
      component: () => import('@/components/' + route.pageName)
    })
  })
}

router.beforeEach((to, from, next) => {
  const token = localStorage.getItem("isLogin");
  if (token) {
    if (to.path === "/login" || to.path === "/login/") {
      routeFlag = false;
      next();
    }
    else if (routeFlag) {
      next();
    } else {
      routeFlag = true;
      addRoutes();
      next('/home');
      // location.reload();
    }
  } else {
    routeFlag = false;
    if (to.path === "/login" || to.path === "/login/") {
      next();
    } else {
      next({ name: 'Login' });
    }
  }
})

export default router
