// import HomePage from '@/components/HomePage.vue'
// import Hello from '@/components/Hello.vue'
import HomePage from '@/components/HomePage.vue';
import UserPage from '@/components/UserPage.vue';
import Login from '@/views/Login.vue'
import MainPage from '@/views/MainPage.vue';

import { createRouter, createWebHashHistory } from 'vue-router'

const routes = [
  {
    path: '/login',
    name: Login,
    component: Login
  },
  {
    path: '/',
    name: MainPage,
    component: MainPage,
    redirect: '/home',
    children: [
      {
        path: '/home',
        name: HomePage,
        component: HomePage
      },
      {
        path: '/user',
        name: UserPage,
        component: UserPage
      }
    ]
  }
]

const router = createRouter({
  history: createWebHashHistory(),
  routes
})

let routeFlag = false;

function addRoutes() {
  const route = localStorage.getItem("routeLists");
  const lists = JSON.parse(route);
  lists.forEach(element => {
    router.addRoute("MainPage",
      {
        path: element.link,
        name: element.pageName,
        component: () => import(`@/components/${element.pageName}`)
      }
    )
  });
}

router.beforeEach((to, from, next) => {
  const token = localStorage.getItem("isLogin");
  if (token) {
    if (routeFlag) {
      next();
    } else {
      routeFlag = true;
      // addRoutes();
      next();
    }
  } else {
    routeFlag = false;
    if (to.name === "login") {
      next();
    } else {
      console.log("dododdo");
      next({ name: Login });
    }
  }
})

export default router
