// import HomePage from '@/components/HomePage.vue'
// import Hello from '@/components/Hello.vue'
import HomePage from '@/components/HomePage.vue';
import UserPage from '@/components/UserPage.vue';
import Login from '@/views/Login.vue'
import MainPage from '@/views/MainPage.vue';

import { createRouter, createWebHashHistory } from 'vue-router'

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/',
    name: 'MainPage',
    component: MainPage,
    // redirect: '/home',
    // children: [
    //   {
    //     path: '/home',
    //     name: 'HomePage',
    //     component: HomePage
    //   },
    //   {
    //     path: '/user',
    //     name: 'UserPage',
    //     component: UserPage
    //   }
    // ]
  }
]

const router = createRouter({
  history: createWebHashHistory(),
  routes
})

let routeFlag = false;

function addRoutes() {
  let routeLists = localStorage.getItem('routeLists');
  console.log(routeLists);

  routeLists.forEach(route => {
    router.addRoute("MainPage", {
      path: route.link,
      name: route.pageName,
      component: () => import('@/components/' + route.pageName)
    })
    //   {
    //     path: '/home',
    //     name: 'HomePage',
    //     component: HomePage
    //   }
  })
}

router.beforeEach((to, from, next) => {
  const token = localStorage.getItem("isLogin");
  if (token) {
    if (routeFlag) {
      next();
    } else {
      routeFlag = true;
      addRoutes();
      next();
    }
  } else {
    routeFlag = false;
    // console.log(to);

    if (to.path === "/login") {
      next();
    } else {
      next({ name: 'Login' });
    }
  }
})

export default router
