// import HomePage from '@/components/HomePage.vue'
// import Hello from '@/components/Hello.vue'
import HomePage from '@/components/HomePage.vue';
import UserPage from '@/components/UserPage.vue';
import Login from '@/views/Login.vue'
import MainPage from '@/views/MainPage.vue';

import { createRouter, createWebHashHistory } from 'vue-router'

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/',
    name: 'MainPage',
    component: MainPage,
    redirect: '/home',
    children: [
      {
        path: '/home',
        name: 'HomePage',
        component: HomePage
      },
      {
        path: '/user',
        name: 'UserPage',
        component: UserPage
      }
    ]
  }
]

const router = createRouter({
  history: createWebHashHistory(),
  routes
})

let routeFlag = false;

function addRoutes() {
  // const route = JSON.stringify(data.data);
  // localStorage.setItem("routeLists", route);

  const routeLists = JSON.parse(localStorage.getItem("routeLists"));
  console.log(routeLists);

}

router.beforeEach((to, from, next) => {
  const token = localStorage.getItem("isLogin");
  if (token) {
    if (routeFlag) {
      next();
    } else {
      routeFlag = true;
      addRoutes();
      next();
    }
  } else {
    routeFlag = false;
    // console.log(to);

    if (to.path === "/login") {
      next();
    } else {
      next({ name: 'Login' });
    }
  }
})

export default router
