// import HomePage from '@/components/HomePage.vue'
// import Hello from '@/components/Hello.vue'
import Login from '@/views/Login.vue'
import MainPage from '@/views/MainPage.vue';

import { createRouter, createWebHashHistory, createWebHistory } from 'vue-router'

const routes = [
  {
    path: '/',
    redirect: '/login',
    component:{
      beforeRouteEnter (to, from, next) {
        addRoutes();
        next();
      }
    }
  },
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/main',
    name: 'MainPage',
    component: MainPage,
  }
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

let routeFlag = false;
//在登录界面加载时就使用
export function resetRouter() {
  return new Promise(() => {
    let routeLists = JSON.parse(localStorage.getItem('routeLists'));
    routeLists.forEach(route => {
      if (router.resolve(route.link) != undefined) {
        console.log(router.resolve(route.link));
        router.removeRoute(route.pageName);
      }
    })
  })

}
function addRoutes() {
  let routeLists = JSON.parse(localStorage.getItem('routeLists'));
  routeLists.forEach(route => {
    router.addRoute("MainPage", {
      path: route.link,
      name: route.pageName,
      component: () => import('@/components/' + route.pageName)
    })
  })
}

router.beforeEach((to, from, next) => {
  const ok = localStorage.getItem('isLogin');

  addRoutes()
  if (ok || from.path == '/') {
    console.log("okkkk");
    next();
  }
  else if (to.path == '/main') {
    next('/home')
  }
  else next()
})

export default router
